const progressFill = document.querySelector('#determinate-progress .progress-fill');
const progressEl = progressFill.closest('[role=progressbar]');

let progress = 25;
const interval = setInterval(() => {
  progress += 10;
  if (progress > 100) progress = 100;

  // Update the visual fill
  progressFill.style.width = `${progress}%`;

  // Update the accessible value
  progressEl.setAttribute('aria-valuenow', progress);

  if (progress === 100) clearInterval(interval);
}, 500);

