const timeline = document.querySelector('.timeline');
const addEventButton = document.querySelector('#add-event-btn');

addEventButton.addEventListener('click', () => {
  const newItem = document.createElement('li');
  newItem.className = 'timeline-item';
  newItem.innerHTML = `
    <time class=\"timeline-time\">Just now</time>
    <h4 class=\"timeline-title\">New User Signed Up</h4>
    <p class=\"timeline-description\">A new user from California has joined.</p>
  `;
  timeline.prepend(newItem);
});

toast.code:
const toastContainer = document.getElementById('toast-container');

function createToast(message, type = 'info') {
  const toast = document.createElement('div');
  toast.className = `toast toast-${type}`;
  toast.textContent = message;

  toastContainer.appendChild(toast);

  // Trigger the show animation
  setTimeout(() => toast.classList.add('show'), 100);

  // Remove the toast after a delay
  setTimeout(() => {
    toast.classList.remove('show');
    toast.addEventListener('transitionend', () => toast.remove());
  }, 3000);
}

document.getElementById('show-success-toast').addEventListener('click', () => {
  createToast('Operation completed successfully!', 'success');
});

breadcrumbs.code:
function updateBreadcrumbs(crumbs) {
  const nav = document.querySelector('.breadcrumb-nav ol');
  nav.innerHTML = ''; // Clear existing crumbs

  crumbs.forEach((crumb, index) => {
    const li = document.createElement('li');
    li.className = 'breadcrumb-item';

    if (index === crumbs.length - 1) {
      li.innerHTML = `<span class=\"breadcrumb-current\" aria-current=\"page\">${crumb.text}</span>`;
    } else {
      li.innerHTML = `<a href=\"${crumb.href}\" class=\"breadcrumb-link\">${crumb.text}</a>`;
    }
    nav.appendChild(li);
  });
}

// Example usage:
// updateBreadcrumbs([{text: 'Home', href: '#'}, {text: 'Settings', href: '#'}]);

