const nav = document.querySelector('.pagination-nav');

nav.addEventListener('click', (e) => {
  e.preventDefault();
  const target = e.target.closest('.pagination-item');

  if (target && !target.classList.contains('disabled') && !target.classList.contains('current')) {
    const current = nav.querySelector('.current');
    if (current) {
      current.classList.remove('current');
      current.removeAttribute('aria-current');
    }

    target.classList.add('current');
    target.setAttribute('aria-current', 'page');

    // In a real app, you would now fetch the data for the new page
    console.log(`Navigating to page ${target.textContent}`);
  }
});

stepper.code:
class Stepper {
    constructor(container) {
        this.container = container;
        this.steps = container.querySelectorAll('.step');
        this.panels = container.querySelectorAll('.stepper-content > div');
        this.prevBtn = container.querySelector('#stepper-prev-btn');
        this.nextBtn = container.querySelector('#stepper-next-btn');
        this.currentStep = 1; // Initial step is 2, index 1
        this.update();
        this.prevBtn.addEventListener('click', () => this.changeStep(-1));
        this.nextBtn.addEventListener('click', () => this.changeStep(1));
    }

    changeStep(change) {
        const newStep = this.currentStep + change;
        if (newStep >= 0 && newStep < this.steps.length) {
            this.currentStep = newStep;
            this.update();
        }
    }

    update() {
        this.steps.forEach((step, index) => {
            step.classList.remove('active', 'completed');
            if (index < this.currentStep) step.classList.add('completed');
            if (index === this.currentStep) step.classList.add('active');
        });
        this.panels.forEach((panel, index) => {
            panel.hidden = index !== this.currentStep;
        });
        this.prevBtn.disabled = this.currentStep === 0;
        this.nextBtn.disabled = this.currentStep === this.steps.length - 1;
    }
}

new Stepper(document.querySelector('.stepper-container'));
